import React, { Component } from "react";

export default class RegistrationForm extends Component {
    constructor(props){
        super(props);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event){ 
        event.preventDefault();
        alert("Your class is being added. Please click the classes button to see your new class")
        fetch('https://garzacao-capstone.uc.r.appspot.com/registrationForm', {
            method: 'post',
            headers: {'Content-Type':'application/json', "Access-Control-Allow-Origin": "*"},
            body: JSON.stringify({
                "name": this.name.value,
                "pronouns": this.pronouns.value,
                "price": this.OSUID.value,
                "member_price": this.member_price.value,
                "teacher": this.teacher.value,
                "datetime": this.datetime.value,
                "capacity": this.capacity.value
                })
            }
        );
    };

    render () {
        return (
            <div id="submit registration">
            <h1>Registration Form:</h1>
            <form onSubmit={this.handleSubmit}>
                <input ref={(ref) => {this.name = ref}} placeholder="Your Name" type="text" name="name"/><br />
                <input ref={(ref) => {this.description = ref}} placeholder="Your Pronouns" type="text" name="pronouns"/><br />
                <input ref={(ref) => {this.price = ref}} placeholder="Current OSU ID Number" type="int" name="OSUID"/><br />
                <input ref={(ref) => {this.member_price = ref}} placeholder="Phone Number" type="int" name="phoneNumber"/><br />
                <input ref={(ref) => {this.teacher = ref}} placeholder="Street Address" type="text" name="streetAddress"/><br />
                <input ref={(ref) => {this.datetime = ref}} placeholder="City" type="text" name="city"/><br />
                <input ref={(ref) => {this.capacity = ref}} placeholder="State" type="text" name="state"/><br />
                <input ref={(ref) => {this.capacity = ref}} placeholder="Zip Code" type="text" name="zipCode"/><br />
                <button type="Submit">Submit Registration</button>
            </form>
            </div>
            )
        }
}
