import React, { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import { CSVLink } from 'react-csv';

function RegistrationForm() {
    const [students, setStudents] = useState([])

    const navigate = useNavigate()
    const URL = 'https://garzacao-capstone.uc.r.appspot.com/students'

    const fetchStudents = async () => {
        // GET request using fetch with async/await
        const response = await fetch(URL);
        const data = await response.json();
        const students = data
        setStudents(students)
        console.log(students)
    }


    const deleteStudents = (student_id) => {
        fetch((URL + '/' + student_id), {
            method: "DELETE"})
        const newStudents = students.filter(
            (student) => student.id !== student_id
        )
        setStudents(newStudents)
    }

    const editStudents = (student_id) => {
        fetch((URL + '/' + student_id), {
            method: "PUT"})
        const newStudents = students.filter(
            (student) => student.id !== student_id
        )
        setStudents(newStudents)
    }

    const addWinterMembership = (student_id) => {
        fetch((URL + '/' + student_id + '/membership'), {
            method: "PUT",
            headers: {'Content-Type':'application/json', "Access-Control-Allow-Origin": "*"},
            body: JSON.stringify({"term":"Wi2022"})
        })

        window.location.reload(false);
    }

    useEffect(() => {
        fetchStudents()
    }, [])

    const registerStudentForClass = (student_id) => {
        navigate(`/students/${student_id}/class`)
    }

    return (
        <>
            <h1>Registration Page</h1>
            <p>
                List of students
            </p>


            <p>
                <a href="/studentAdd">Submit Registration</a>
            </p>
        </>
    )
}

export default RegistrationForm
